<?phpdefined('BASEPATH') or exit('No direct script access');if ( !function_exists('format_date') ){    /**     * Generate Unix time to formated date     *     * @param int $time     * @param bool $hour     * @return string     */    function buildDayDropdown($name='',$value='')    {	$day = array(	'01' => '1',        '02' => '2',        '03' => '3',        '04' => '4',	'05' => '5',        '06' => '6',        '07' => '7',        '08' => '8',        '09' => '9',        '10' => '10',        '11' => '11',        '12' => '12',        '13' => '13',        '14' => '14',        '15' => '15',        '16' => '16',	'17' => '17',        '18' => '18',        '19' => '19',        '20' => '20',        '21' => '21',        '22' => '22',        '23' => '23',        '24' => '24',        '25' => '25',        '26' => '26',        '27' => '27',        '28' => '28',	'29' => '29',        '30' => '30',        '31' => '31'    );		        return form_dropdown($name, $day, $value);    }    function buildMonthDropdown($name='',$value='')    {	$month = array(        '01' => 'Januari',        '02' => 'Februari',        '03' => 'Maret',        '04' => 'April',	'05' => 'Mei',        '06' => 'Juni',        '07' => 'Juli',        '08' => 'Agustus',        '09' => 'September',        '10' => 'Oktober',        '11' => 'November',        '12' => 'Desember'    );		        return form_dropdown($name, $month, $value);    }    function buildYearDropdown($name='',$value='')    {	$year = array(	  '2009' => '2009',	  '2010' => '2010',	  '2011' => '2011',	  '2012' => '2012'    	);		        return form_dropdown($name, $year, $value);    }    function format_date($time = 0, $hour = false)    {        $CI = & get_instance();        $CI->load->language('calendar');        // ambil indeks bahasa dari calendar_lang.php        $months = array(            '01' => $CI->lang->line('cal_januari'),            '02' => $CI->lang->line('cal_februari'),            '03' => $CI->lang->line('cal_maret'),            '04' => $CI->lang->line('cal_april'),            '05' => $CI->lang->line('cal_mei'),            '06' => $CI->lang->line('cal_juni'),            '07' => $CI->lang->line('cal_juli'),            '08' => $CI->lang->line('cal_agustus'),            '09' => $CI->lang->line('cal_september'),            '10' => $CI->lang->line('cal_oktober'),            '11' => $CI->lang->line('cal_november'),            '12' => $CI->lang->line('cal_desember'),        );                $day = array(            '0' => $CI->lang->line('cal_ahad'),            '1' => $CI->lang->line('cal_senin'),            '2' => $CI->lang->line('cal_selasa'),            '3' => $CI->lang->line('cal_rabu'),            '4' => $CI->lang->line('cal_kamis'),            '5' => $CI->lang->line('cal_jumat'),            '6' => $CI->lang->line('cal_sabtu'),          );        $month = $months[date('m', $time)];        $day = $day[date('w', $time)];        if ( $hour == true )        {            // menampilkan tanggal dan jam            return sprintf('%s %s %s %s %s', $day ,date('d', $time), $month, date('Y', $time), date('H:i', $time));        }        // menampilkan tanggal saja        return sprintf('%s %s %s', date('d', $time), $month, date('Y', $time));    }        function convertHijri($date,$monthname=false) {       $y = substr($date,0,4);       $m = substr($date,5,2);       $d = substr($date,8,2);      if (($y>1582)||(($y==1582)&&($m>10))||(($y==1582)&&($m==10)&&($d>14))){        $jd=(int)((1461*($y+4800+(int)(($m-14)/12)))/4)+(int)((367*($m-2-12*((int)(($m-14)/12))))/12)-(int)( (3* ((int)(  ($y+4900+    (int)( ($m-14)/12))/100)))/4)+$d-32075;      } else {        $jd = 367*$y-(int)((7*($y+5001+(int)(($m-9)/7)))/4)+(int)((275*$m)/9)+$d+1729777;      }            $l=$jd-1948440+10632;            $n=(int)(($l-1)/10631);            $l=$l-10631*$n+354;            $j=((int)((10985-$l)/5316))*((int)((50*$l)/17719))+((int)($l/5670))*((int)((43*$l)/15238));            $l=$l-((int)((30-$j)/15))*((int)((17719*$j)/50))-((int)($j/16))*((int)((15238*$j)/43))+29;            $m=(int)((24*$l)/709);            $d=$l-(int)((709*$m)/24);            $y=30*$n+$j-30;  if(!$monthname){ // return basic    return($d.'-'.$m.'-'.$y);  } else { // return full    switch ($m){      case 1:        $mn = 'Muharram';      break;      case 2:        $mn = 'Safar';      break;      case 3:        $mn = 'Rabiulawal';      break;      case 4:        $mn = 'Rabiulakhir';      break;      case 5:        $mn = 'Jamadilawal';      break;      case 6:        $mn = 'Jamadilakhir ';      break;      case 7:        $mn = 'Rejab';      break;      case 8:        $mn = 'Syaaban';      break;      case 9:        $mn = 'Ramadhan';      break;      case 10:        $mn = 'Syawal';      break;      case 11:        $mn = 'Zulkaedah';      break;      case 12:        $mn = 'Zulhijjah';      break;                }    return($d.' '.$mn.', '.$y);  }  }}/* end of file application/helpers/MY_date_helper.php */